(function($){$.fn.simpleCheckbox=function(options){var defaults={newElementClass:"tog",activeElementClass:"on"};var options=$.extend(defaults,options);this.each(function(){var obj=$(this);var newObj=$("<div/>",{id:"#"+obj.attr("id"),class:options.newElementClass,style:"display: block;"}).insertAfter(this);if(obj.is(":checked")){newObj.addClass(options.activeElementClass)}obj.hide();if($("[for="+obj.attr("id")+"]").length){var label=$("[for="+obj.attr("id")+"]");label.click(function(){newObj.trigger("click");return false})}newObj.click(function(){var obj=$(this);if(obj.hasClass(options.activeElementClass)){obj.removeClass(options.activeElementClass);$(obj.attr("id")).attr("checked",false)}else{obj.addClass(options.activeElementClass);$(obj.attr("id")).attr("checked",true)}return false})})}})(jQuery);jQuery(document).ready(function($){$("input:checkbox").simpleCheckbox()});